import styled from "styled-components"
import { ChevronLeftOutlined, ChevronRightOutlined } from '@material-ui/icons'
import React from 'react'

const Container = styled.div`
    width: 100%;
    height: 100vh;
    display: flex;
    position: relative;

`
const Arrow = styled.div`
    width: 50px;
    height: 50px;
    background-color: white;
    border-radius: 50%;
    display:flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 0;
    bottom: 0;
    left: ${props=> props.direction === "left" && "30px"};
    right: ${props=> props.direction === "right" && "30px"};
    margin: auto;
    cursor:pointer;
    opacity: 0.7;
`
const Wrapper = styled.div`
    height; 100%;
`
const Slide = styled.div`
    display: flex;
    align-items: center;
`

const ImgContainer = styled.div`
    flex:1;
`
const Image = style.img`


`

const InfoContainer = styled.div`
    flex:1;
`

function Slider() {
  return (
    <Container>
        <Arrow direction="left">
            <ChevronLeftOutlined/>
        </Arrow>
        <Wrapper>
            <ImgContainer>
                <Image src="https://static.massimodutti.net/3/cms/assets/uploads/desktopWLimitedEdition_2.jpg?impolicy=massimodutti-itxmediumhigh&imwidth=1400&imformat=chrome"></Image>
            </ImgContainer>
            <InfoContainer></InfoContainer>
        </Wrapper>
        <Arrow direction="right">
            <ChevronRightOutlined/>
        </Arrow>
    </Container>
  )
}

export default Slider
